import { takeLatest, put, delay, takeEvery, call } from "redux-saga/effects";
import {
  AUTH_FAIL,
  AUTH_SUCCESS,
  AUTH_VALIDATION,
  REGISTER,
  UPDATE_MESSAGES,
  UPDATE_MESSAGE,
  GET_DATA
} from "../actions/actions";

import * as firebase from "firebase";

export function* register(action) {
  yield delay(1000);
  const { email, password } = action.payload;
  console.log(`Register details : ${email},${password}`);
}

export function* update(action) {
  console.log("Message updated");
  console.log(action);
  const user1 = action.data.name;
  console.log({
    [user1]: {
      email: action.data.email,
      name: user1,
      message: action.data.message
    }
  });

  firebase
    .database()
    .ref("details")
    .push({
      [user1]: {
        email: action.data.email,
        name: user1,
        message: action.data.message
      }
    });

  let data;
  var recentPostsRef = firebase.database().ref("/details");
  yield recentPostsRef.once("value").then(snapshot => {
    data = snapshot.val();
  });

  console.log({ data, type: UPDATE_MESSAGES });
  yield put({ data, type: UPDATE_MESSAGES });
}

export function* getData(action) {
  var recentPostsRef = firebase.database().ref("/details");
  recentPostsRef.once("value").then(snapshot => {
    console.log(snapshot.val());
    // this.setState({ stores: snapshot.val() })
  });
}

export function* sendAction(data) {
  console.log(data);
  yield put({ data, type: AUTH_SUCCESS });
}
export function* validateLogin(action) {
  // console.log(action);
  yield delay(1000);
  const { email, password } = action.payload;
  const temp = [
    { id: 1, email: action.payload.email, name: "abc", message: "demo" }
  ];
  let data;
  if (email === "a@b.c" && password === "123") {
    console.log("AUTH_success ");
    var recentPostsRef = firebase.database().ref("/details");
    yield recentPostsRef.once("value").then(snapshot => {
      data = snapshot.val();
    });
    yield call(sendAction, data);
  } else yield put({ ...action, type: AUTH_FAIL });
}

export default function* rootSaga() {
  yield takeLatest(AUTH_VALIDATION, validateLogin);
  yield takeEvery(GET_DATA, getData);
  yield takeLatest(UPDATE_MESSAGE, update);
  yield takeLatest(REGISTER, register);
}
