import React, { useState } from "react";
import Details from "./components/Details/Details";
import Chat from "./components/Chat/Chat";
import chatIcon from "./assets/chatIcon.jpg";
import closeIcon from "./assets/close.jpg";
import { connect } from "react-redux";
import Login from "./components/Login/Login";
import "./App.css";

function App(props) {
  let [displayChat, setToggle] = useState(false);

  const toggleChat = e => {
    setToggle(prevState => !prevState);
  };

  return (
    <div className="App">
      {props.isLogged ? <Details /> : <Login />}
      {displayChat ? <Chat /> : null}
      <div className="chat-icon">
        <img
          src={displayChat ? closeIcon : chatIcon}
          alt="Chat Icon"
          onClick={e => toggleChat(e)}
        />
      </div>
    </div>
  );
}
const mapStateToProps = state => ({
  isLogged: state.isLogged,
  loginStatus: state.loginStatus
});

export default connect(
  mapStateToProps,
  null
)(App);
