import React, { useState } from "react";
import back_arrow from "../../assets/left-arrow.svg";
import help_scout from "../../assets/helpScout.jpg";
import plane from "../../assets/plane1.svg";
import Avatars from "../Avatars/Avatars";
import ContactForm from "../ContactForm/ContactForm";
import classes from "./Chat.module.css";
import Previous from "../PreviousChat/Previous";

const Chat = props => {
  let [toggleForm, setToggle] = useState("previous");
  const toggleController = type => {
    setToggle(type);
  };

  const style = props.show
    ? { opacity: "1" }
    : { opacity: "0", visibility: "hidden" };

  let header, body;
  if (toggleForm === "form") {
    header = <HeaderForm sended={false} toggle={toggleController} />;
    body = <ContactForm toggle={toggleController} />;
  } else if (toggleForm === "email") {
    header = <HeaderEmail />;
    body = <EmailCard toggle={toggleController} />;
  } else if (toggleForm === "sended") {
    header = <HeaderForm sended={true} toggle={toggleController} />;
    body = <MessageSended toggle={toggleController} />;
  } else if (toggleForm === "previous") {
    header = <PreviousHeader toggle={toggleController} />;
    body = <Previous />;
  }

  return (
    <div
      style={style}
      className={[classes.container, classes.style2].join(" ")}
    >
      <div
        className={classes.header}
        style={toggleForm === "sended" ? { "padding-bottom": 0 } : null}
      >
        {header}
      </div>
      {body}
    </div>
  );
};

const HeaderEmail = () => (
  <>
    <div className={classes.header_nav}>
      <span className={classes.title}>
        <p>Start a conversation</p>
      </span>
    </div>
    <Avatars />
    <div className={classes.header_content}>
      <span>What channel do you prefer?</span>
    </div>
  </>
);

const HeaderForm = props => {
  return (
    <>
      <div className={classes.header_nav}>
        <span className={classes.back_arrow}>
          <img
            src={back_arrow}
            alt="back_arrow"
            onClick={() => props.toggle("email")}
          />
        </span>
        <span className={classes.title}>
          {!props.sended ? <p>Start a conversation</p> : <p>Send a message</p>}
        </span>
      </div>
      <Avatars />
      <div className={classes.header_content}>
        <span className={classes.content_1}>
          {!props.sended ? "How can we help?" : "Start a conversation"}
        </span>
        <span className={classes.content_2}>
          We usually respond in a few hours
        </span>
      </div>
    </>
  );
};

const EmailCard = props => {
  return (
    <>
      <div className={classes.email_card} onClick={() => props.toggle("form")}>
        <img src={plane} alt="Plane " />
        <div className={classes.email_content}>
          <span className={classes.heading}>Email</span>
          <br />
          <span className={classes.sub_heading}>
            No time to wait around? We usually respond within a few hours
          </span>
        </div>
      </div>
      <div className={classes.link_button} style={{ marginTop: "55%" }}>
        <a href="/">
          <img
            src={help_scout}
            alt="Help Scout"
            title="Powered by Help Scout"
          />
        </a>
      </div>
    </>
  );
};

const MessageSended = props => {
  return (
    <>
      <div className={classes.circle}></div>
      <div className={classes.AfterSend}>
        <span>We’re on it!</span>
        <p>
          You’ll receive an email reply within a few hours. You can view and
          update your message in{" "}
          <span
            className={classes.previous}
            onClick={() => props.toggle("previous")}
          >
            Previous Conversations.
          </span>
        </p>
      </div>
      <div className={classes.link_button} style={{ marginTop: 0 }}>
        <a href="/">
          <img
            src={help_scout}
            alt="Help Scout"
            title="Powered by Help Scout"
          />
        </a>
      </div>
    </>
  );
};

const PreviousHeader = props => {
  return (
    <div className={classes.header_nav} style={{ height: "20px" }}>
      <span className={classes.back_arrow}>
        <img
          src={back_arrow}
          alt="back_arrow"
          onClick={() => props.toggle("sended")}
        />
      </span>
      <span
        style={{
          color: "white",
          textAlign: "center",
          display: "flex",
          justifyContent: "center",
          alignItems: "center"
        }}
      >
        <p>Previous</p>
      </span>
    </div>
  );
};

export default Chat;
