import React, { useState } from "react";
import Details from "./components/Details/Details";
import Chat from "./components/Chat/Chat";
import chatIcon from "./assets/comment.svg";
import "./App.css";
function App() {
  let [displayChat, setToggle] = useState(false);

  const toggleChat = e => {
    // setToggle(prevState => !prevState);
    event.target.display = false;
  };
  return (
    <div className="App">
      {/* <Details /> */}
      {displayChat ? <Chat /> : null}
      <div className="chat-icon">
        <img src={chatIcon} alt="Chat Icon" onClick={e => toggleChat(e)} />
      </div>
    </div>
  );
}

export default App;
