import React, { useState } from "react";
import { connect } from "react-redux";
import classes from "./Login.module.css";
import { login, register } from "../../actions/actions";
import "loaders.css";
// import Spinner from "react-spinkit";
import spinClasses from "./spinner.module.css";

const Login = props => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [isLogin, setIsLogin] = useState(true);

  const reset = () => {
    setEmail("");
    setPassword("");
    setName("");
  };

  return props.loading ? (
    <div className={classes.loader}>
      <div className={spinClasses.spinner}>
        <div className={spinClasses.rect1}></div>
        <div className={spinClasses.rect2}></div>
        <div className={spinClasses.rect3}></div>
        <div className={spinClasses.rect4}></div>
        <div className={spinClasses.rect5}></div>
      </div>{" "}
      {/* <Spinner name="circle" /> */}
    </div>
  ) : (
    <form
      onSubmit={event => {
        event.preventDefault();
        isLogin
          ? props.authLogin(email, password)
          : props.register(name, email, password);
      }}
    >
      <div className={classes.Login}>
        {isLogin ? null : (
          <>
            <label>Username </label>
            <input
              className={classes.text}
              type="text"
              placeholder="Username"
              name="username"
              value={name}
              onChange={e => setName(e.target.value.trim())}
              required
            />
          </>
        )}
        <label>Email Id </label>
        <input
          className={classes.text}
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value.trim())}
          required
        />
        <label>Password </label>
        <input
          className={classes.text}
          type="password"
          name="password"
          value={password}
          placeholder="Password"
          onChange={e => setPassword(e.target.value)}
          required
        />
        {isLogin ? (
          <>
            {props.loginStatus === "error" ? (
              <center>
                <div className={classes.error}>Invalid credentials</div>
              </center>
            ) : null}
            <button>Login</button>
            <p className={classes.register}>
              Not registered ?
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setIsLogin(false);
                  reset();
                }}
              >
                Create an account
              </span>
            </p>
          </>
        ) : (
          <>
            <button>Register</button>
            <p className={classes.register}>
              Already have an account?
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setIsLogin(true);
                  reset();
                }}
              >
                Login here
              </span>
            </p>
          </>
        )}
      </div>
    </form>
  );
};

const mapDispatchToProps = dispatch => ({
  authLogin: (email, password) => dispatch(login(email, password)),
  register: (name, email, password) => dispatch(register(name, email, password))
});
const mapStateToProps = state => ({
  loading: state.loading,
  loginStatus: state.loginStatus
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
