import React, { useRef, useEffect, useState } from "react";
import help_scout from "../../assets/helpScout.jpg";
import add_image from "../../assets/Add_Image.png";
import classes from "./ContactForm.module.css";
const ContactForm = () => {
  const nameRef = useRef(null);

  useEffect(() => {
    nameRef.current.focus();
  }, []);

  let [name, setName] = useState("");
  let [email, setEmail] = useState("");
  let [subject, setSubject] = useState("");
  let [message, setMessage] = useState("");

  return (
    <>
      <form>
        <div className={classes.contact_form}>
          <div className={classes.input}>
            <input
              className={classes.text}
              type="text"
              placeholder="Name"
              name="name"
              ref={nameRef}
              onChange={e => setName(e.target.value)}
            />
            <input
              className={classes.text}
              type="text"
              placeholder="Subject"
              name="subject"
              onChange={e => setSubject(e.target.value)}
            />
            <input
              className={classes.text}
              type="email"
              placeholder="Email"
              name="email"
              onChange={e => setEmail(e.target.value)}
            />
            <div className={classes.message_area}>
              <textarea
                placeholder="How can we help?"
                name="message"
                onChange={e => setMessage(e.target.value)}
              ></textarea>
              <img src={add_image} alt="Image add" />
            </div>
            <div className={classes.message_button}>
              <input
                type="submit"
                className={classes.input}
                value="Send a message"
              />
            </div>
          </div>
        </div>
      </form>
      <div className={classes.link_button} style={{ "margin-top": "10px" }}>
        <a href="#">
          <img
            src={help_scout}
            alt="Help Scout logo"
            title="Powered by Help Scout"
          />
        </a>
      </div>
    </>
  );
};

export default ContactForm;
