import React, { useState } from "react";
import Details from "./components/Details/Details";
import Chat from "./components/Chat/Chat";
import chatIcon from "./assets/chatBubble.svg";
import closeIcon from "./assets/cancel.svg";
import { connect } from "react-redux";
import Login from "./components/Login/Login";
import "./App.css";

function App(props) {
  let [displayChat, setToggle] = useState(false);

  const toggleChat = e => {
    setToggle(prevState => !prevState);
  };

  let style;
  if (displayChat) {
    style = {
      opacity: 1,
      transition: "all .5s linear",
      transform: "rotate(60deg)"
    };
  } else {
    // style = { opacity: 0 };
  }
  return (
    <div className="App">
      {props.isLogged ? <Details /> : <Login />}
      <Chat show={displayChat} />
      <div className="Icon" onClick={e => toggleChat(e)}>
        {displayChat ? (
          <img
            className="closeIcon"
            style={{ ...style, height: "15px", width: "15px" }}
            src={closeIcon}
            alt="Chat Icon"
          />
        ) : (
          <img src={chatIcon} style={style} alt="Chat Icon" />
        )}
      </div>
    </div>
  );
}
const mapStateToProps = state => ({
  isLogged: state.isLogged,
  loginStatus: state.loginStatus
});

export default connect(
  mapStateToProps,
  null
)(App);
