import { AUTH_SUCCESS, AUTH_FAIL, UPDATE_MESSAGES } from "../actions/actions";

const INITIAL_STATE = {
  isLogged: false,
  data: {
    name: "",
    id: ""
  },
  loginStatus: ""
};

const authReducer = (state = INITIAL_STATE, action) => {
  console.log(action);
  switch (action.type) {
    case AUTH_SUCCESS:
      return {
        isLogged: true,
        data: action.data,
        loginStatus: "success"
      };
    case AUTH_FAIL:
      return {
        isLogged: false,
        data: {},
        loginStatus: "error"
      };
    case UPDATE_MESSAGES:
      console.log(action);
      return {
        isLogged: true,
        data: action.data,
        loginStatus: "success"
      };

    default:
      return state;
  }
};

export default authReducer;
