import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import classes from "./Login.module.css";
import { login, register, AUTH_SUCCESS } from "../../actions/actions";
import "./spinner.css";

const Login = props => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [isLogin, setIsLogin] = useState(true);

  const reset = () => {
    setEmail("");
    setPassword("");
    setName("");
  };

  useEffect(() => {
    if (localStorage.getItem("isLogged")) {
      const data = JSON.parse(localStorage.getItem("data"));
      props.logged(data);
    }
  }, []);

  return props.loading ? (
    <div className={classes.loader}>
      <div className="sk-circle">
        <div className="sk-circle1 sk-child"></div>
        <div className="sk-circle2 sk-child"></div>
        <div className="sk-circle3 sk-child"></div>
        <div className="sk-circle4 sk-child"></div>
        <div className="sk-circle5 sk-child"></div>
        <div className="sk-circle6 sk-child"></div>
        <div className="sk-circle7 sk-child"></div>
        <div className="sk-circle8 sk-child"></div>
        <div className="sk-circle9 sk-child"></div>
        <div className="sk-circle10 sk-child"></div>
        <div className="sk-circle11 sk-child"></div>
        <div className="sk-circle12 sk-child"></div>
      </div>
      {/* <Spinner name="circle" /> */}
    </div>
  ) : (
    <div>
      <form
        className={classes.Login}
        onSubmit={event => {
          event.preventDefault();
          if (isLogin) {
            props.authLogin(email, password);
          } else {
            const regex = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/;
            const pwd = document.querySelector("#password").value;
            if (regex.test(pwd)) {
              console.log("success");
              props.register(name, email, password);
              setIsLogin(true);
              reset();
            } else {
              console.log("not");
              document.querySelector(".password").style.display = "block";
            }
          }
        }}
      >
        {isLogin ? null : (
          <>
            <label>Username </label>
            <input
              className={classes.text}
              type="text"
              placeholder="Username"
              name="username"
              value={name}
              onChange={e => setName(e.target.value.trim())}
              required
            />
          </>
        )}
        <label>Email Id </label>
        <input
          className={classes.text}
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value.trim())}
          required
        />
        <label>Password </label>
        <input
          className={classes.text}
          type="password"
          name="password"
          id="password"
          value={password}
          placeholder="Password"
          onChange={e => setPassword(e.target.value)}
          required
        />
        {isLogin ? null : (
          <center>
            <div
              className="password"
              style={{ fontSize: "12px", fontWeight: "bold", display: "none" }}
            >
              Password must contain atleast one symbol,number,char
            </div>
          </center>
        )}

        {isLogin ? (
          <>
            {props.loginStatus === "error" ? (
              <center>
                <div className={classes.error}>Invalid credentials</div>
              </center>
            ) : null}
            <button>Login</button>
            <p className={classes.register}>
              Not registered ?
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setIsLogin(false);
                  reset();
                }}
              >
                Create an account
              </span>
            </p>
          </>
        ) : (
          <>
            <button>Register</button>
            <p className={classes.register}>
              Already have an account?
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setIsLogin(true);
                  reset();
                }}
              >
                Login here
              </span>
            </p>
          </>
        )}
      </form>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  authLogin: (email, password) => dispatch(login(email, password)),
  register: (name, email, password) =>
    dispatch(register(name, email, password)),
  logged: data => dispatch({ type: AUTH_SUCCESS, data: data })
});
const mapStateToProps = state => ({
  loading: state.loading,
  loginStatus: state.loginStatus
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
