import React, { useState } from "react";
import classes from "./Chat.module.css";
import back_arrow from "../../assets/left-arrow.svg";

import help_scout from "../../assets/help_scout.png";

import Avatars from "../Avatars/Avatars";
import ContactForm from "../ContactForm/ContactForm";

const Chat = props => {
  let [toggleForm, setToggle] = useState(false);
  const toggleController = () => {
    setToggle(prevState => !prevState);
  };
  return (
    <div className={classes.container}>
      <div className={classes.header}>
        {toggleForm ? <HeaderForm /> : <HeaderEmail />}
      </div>
      {toggleForm ? <ContactForm /> : null}

      <div className={classes.link_button}>
        <a href="#">
          <img
            src={help_scout}
            alt="Help Scout logo"
            title="Powered by Help Scout"
          />
        </a>
      </div>
    </div>
  );
};

const HeaderEmail = () => (
  <>
    <div className={classes.header_nav}>
      <span className={classes.title}>
        <p>Send a message</p>
      </span>
    </div>
    <Avatars />
    <div className={classes.header_content}>
      <span>What channel do you prefer?</span>
    </div>
  </>
);
const HeaderForm = () => (
  <>
    <div className={classes.header_nav}>
      <span className={classes.back_arrow}>
        <img src={back_arrow} alt="back_arrow" />
      </span>
      <span className={classes.title}>
        <p>Send a message</p>
      </span>
    </div>
    <Avatars />
    <div className={classes.header_content}>
      <span className={classes.content_1}>How can we help?</span>
      <span className={classes.content_2}>
        We usually respond in a few hours
      </span>
    </div>
  </>
);

export default Chat;
