import React, { useEffect } from "react";
import classes from "./Details.module.css";
import Detail from "../Detail/Detail";
import { connect } from "react-redux";
import Login from "../Login/Login";
import * as firebase from "firebase";
import { logout } from "../../actions/actions";
import Logout from "../../assets/logout.svg";
const Details = props => {
  let data, keys;

  useEffect(() => {
    firebase.auth().onAuthStateChanged(user => {
      if (user) {
        console.log("USER LOGGED IN");
      } else {
        console.log("Logout");
        localStorage.removeItem("data");
        localStorage.removeItem("isLogged");
        props.logout();
      }
    });
  }, []);
  if (props.data !== undefined) {
    data = props.data;
    keys = Object.keys(data);
  }
  let list;

  if (props.isLogged) {
    const loggedUser = "naveen";
    list = keys.map(key => {
      if (data[key][loggedUser] === undefined) return;
      return (
        <Detail
          id={key}
          name={data[key][loggedUser].name}
          message={data[key][loggedUser].message}
          subject={data[key][loggedUser].subject}
          email={data[key][loggedUser].email}
        />
      );
    });
  } else {
    console.log("not logged");
    let users = Object.keys(keys);
    list = users.map(key => {
      return (
        <Detail
          id={key}
          name={data[key][users].name}
          message={data[key][users].message}
          subject={data[key][users].subject}
          email={data[key][users].email}
        />
      );
    });
  }
  return (
    <div>
      {props.isLogged ? (
        <>
          <center>
            <h2>Records</h2>
          </center>
          <center>
            <table className={classes.table}>
              <tbody>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Subject</th>
                  <th>Message</th>
                </tr>
                {list}
              </tbody>
            </table>
            <div
              className={classes.logout}
              onClick={() => firebase.auth().signOut()}
            >
              <img src={Logout} alt="Logout" title="Logout" />
            </div>
          </center>
        </>
      ) : (
        <Login />
      )}
    </div>
  );
};
const mapStateToProps = state => ({
  loading: state.loading,
  isLogged: state.isLogged || localStorage.getItem("isLogged"),
  data: state.data || localStorage.getItem("data")
});
const mapDispatchToProps = dispatch => ({
  logout: () => dispatch(logout())
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Details);
