import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import classes from "./Login.module.css";
import { login, register, AUTH_SUCCESS } from "../../actions/actions";
import Spinner from "./Spinner";

const Login = props => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [isLogin, setIsLogin] = useState(true);

  const reset = () => {
    setEmail("");
    setPassword("");
    setName("");
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    if (isLogin) {
      props.authLogin(email, password);
    } else {
      const regex = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/;
      const pwd = document.querySelector("#password").value;
      if (regex.test(pwd)) {
        props.register(name, email, password);
        setTimeout(() => {
          setIsLogin(true);
          reset();
        }, 2000);
      } else {
        document.querySelector(".password").style.display = "block";
      }
    }
  };
  /*eslint-disable */

  useEffect(() => {
    if (localStorage.getItem("isLogged")) {
      const data = JSON.parse(localStorage.getItem("data"));
      props.logged(data);
    }
  }, []);

  /*eslint-enable */

  return props.loading ? (
    <Spinner />
  ) : (
    <div>
      <div>LOGIN</div>
      <form
        className={classes.Login}
        onSubmit={event => formSubmitHandler(event)}
      >
        {isLogin ? null : (
          <>
            {/* <label>Username </label> */}
            <input
              className={classes.text}
              type="text"
              placeholder="Username"
              name="username"
              value={name}
              onChange={e => setName(e.target.value.trim())}
              required
            />
          </>
        )}
        {/* <label>Email Id </label> */}
        <input
          className={classes.text}
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value.trim())}
          required
        />
        {/* <label>Password </label> */}
        <input
          className={classes.text}
          type="password"
          name="password"
          id="password"
          value={password}
          placeholder="Password"
          onChange={e => setPassword(e.target.value)}
          required
        />
        {isLogin ? null : (
          <center>
            <div
              className={[classes.error, "password"].join(" ")}
              style={{ fontSize: "12px", fontWeight: "bold", display: "none" }}
            >
              Password must contain atleast one symbol,number,char
            </div>
          </center>
        )}

        {isLogin ? (
          <>
            {props.loginStatus === "error" ? (
              <center>
                <div className={classes.error}>Invalid credentials</div>
              </center>
            ) : null}
            <button>Login</button>
            <p className={classes.register}>
              Not registered ?
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setIsLogin(false);
                  reset();
                }}
              >
                Create an account
              </span>
            </p>
          </>
        ) : (
          <>
            <button>Register</button>
            <p className={classes.register}>
              Already have an account?
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setIsLogin(true);
                  reset();
                }}
              >
                Login here
              </span>
            </p>
          </>
        )}
      </form>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  authLogin: (email, password) => dispatch(login(email, password)),
  register: (name, email, password) =>
    dispatch(register(name, email, password)),
  logged: data => dispatch({ type: AUTH_SUCCESS, data: data })
});
const mapStateToProps = state => ({
  loading: state.loading,
  loginStatus: state.loginStatus
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);
