import { takeLatest, put, delay } from "redux-saga/effects";

export function* register(action) {
  yield delay(1000);
  const { name, password } = action.payload;
  console.log(`Register details : ${name},${password}`);
}

export function* validateLogin(action) {
  yield delay(1000);
  const { name, password } = action.payload;
  if (name === "abc" && password === "123")
    yield put({ ...action, type: AUTH_SUCCESS });
  else yield put({ ...action, type: AUTH_FAIL });
}

export default function* rootSaga() {
  yield takeLatest(AUTH_VALIDATION, validateLogin);
  yield takeLatest(REGISTER, register);
}
