import { AUTH_SUCCESS, AUTH_FAIL, UPDATE_MESSAGE } from "../actions/actions";

const INITIAL_STATE = {
  isLogged: false,
  data: {
    name: "",
    id: ""
  },
  loginStatus: ""
};

const authReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case AUTH_SUCCESS:
      const temp = [
        { id: 1, email: action.payload.email, name: "abc", message: "demo" }
      ];
      return {
        isLogged: true,
        data: temp,
        loginStatus: "success"
      };
    case AUTH_FAIL:
      return {
        isLogged: false,
        data: {},
        loginStatus: "error"
      };
    case UPDATE_MESSAGE:
      return {
        isLogged: true,
        data: action.payload.data,
        loginStatus: "success"
      };

    default:
      return state;
  }
};

export default authReducer;
