import { takeLatest, put, delay, takeEvery, call } from "redux-saga/effects";
import {
  AUTH_FAIL,
  AUTH_SUCCESS,
  AUTH_VALIDATION,
  REGISTER,
  UPDATE_MESSAGES,
  UPDATE_MESSAGE,
  GET_DATA
} from "../actions/actions";

import * as firebase from "firebase";

export function* register(action) {
  console.log(`Register details : ${email},${password}`);
  firebase
    .auth()
    .createUserWithEmailAndPassword(email, password)
    .then(user => console.log("Registered successfully"))
    .catch();
}

export function* update(action) {
  console.log("Message updated");
  console.log(action);
  const user1 = action.data.name;
  console.log({
    [user1]: {
      email: action.data.email,
      name: user1,
      message: action.data.message
    }
  });

  firebase
    .database()
    .ref("details")
    .push({
      [user1]: {
        email: action.data.email,
        name: user1,
        message: action.data.message
      }
    });

  let data;
  const recentPostsRef = firebase.database().ref("/details");
  yield recentPostsRef.once("value").then(snapshot => {
    data = snapshot.val();
  });
  yield put({ data, type: UPDATE_MESSAGES });
}

async function authenticateLogin(email, password) {
  console.log(email, password);
  return await firebase.auth().signInWithEmailAndPassword(email, password);
}

function* validateLogin(action) {
  const { email, password } = action.payload;
  let data;
  try {
    yield call(authenticateLogin, email, password);
    let recentPostsRef = firebase.database().ref("/details");
    yield recentPostsRef.once("value").then(snapshot => {
      data = snapshot.val();
    });
    yield put({ data, type: AUTH_SUCCESS });
  } catch (e) {
    console.log(e);
    yield put({ type: AUTH_FAIL });
  }

  /*if () {
    console.log("AUTH_SUCCESS ");
    let recentPostsRef = firebase.database().ref("/details");
    yield recentPostsRef.once("value").then(snapshot => {
      data = snapshot.val();
    });
    yield put({ data, type: AUTH_SUCCESS });
  } else yield put({ ...action, type: AUTH_FAIL });
  */
}

export default function* rootSaga() {
  yield takeLatest(AUTH_VALIDATION, validateLogin);
  yield takeLatest(UPDATE_MESSAGE, update);
  yield takeLatest(REGISTER, register);
}
