import { AUTH_SUCCESS, AUTH_FAIL } from "../actions/actions";

const INITIAL_STATE = {
  isLogged: false,
  data: {
    name: "",
    id: ""
  },
  loginStatus: ""
};
const authReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case AUTH_SUCCESS:
      return {
        isLogged: true,
        data: action.payload,
        loginStatus: "success"
      };
    case AUTH_FAIL:
      return {
        isLogged: false,
        data: {},
        loginStatus: "error"
      };
    default:
      return state;
  }
};

export default authReducer;
