import React, { useRef, useEffect, useState } from "react";
import help_scout from "../../assets/helpScout.jpg";
import add_image from "../../assets/Add_Image.png";
import classes from "./ContactForm.module.css";
import { addMsg } from "../../actions/actions";
import { connect } from "react-redux";
// import "./CustomFileInput";

const realFileBtn = document.getElementById("real-file");
const customBtn = document.getElementById("custom-button");
const customTxt = document.getElementById("custom-text");

customBtn.addEventListener("click", function() {
  realFileBtn.click();
});

realFileBtn.addEventListener("change", function() {
  if (realFileBtn.value) {
    customTxt.innerHTML = realFileBtn.value.match(
      /[\/\\]([\w\d\s\.\-\(\)]+)$/
    )[1];
  } else {
    customTxt.innerHTML = "No file chosen, yet.";
  }
});

const ContactForm = props => {
  const nameRef = useRef(null);

  useEffect(() => {
    nameRef.current.focus();
  }, []);

  let [name, setName] = useState("");
  let [email, setEmail] = useState("");
  let [subject, setSubject] = useState("");
  let [message, setMessage] = useState("");

  const submitHandler = e => {
    e.preventDefault();
    props.addMessage(name, email, message, subject);
    props.toggle("sended");
  };
  return (
    <>
      <form onSubmit={e => submitHandler(e)}>
        <div className={classes.contact_form}>
          <div className={classes.input}>
            <input
              className={classes.text}
              type="text"
              placeholder="Name"
              name="name"
              ref={nameRef}
              onChange={e => setName(e.target.value)}
              required
            />
            <input
              className={classes.text}
              type="text"
              placeholder="Subject"
              name="subject"
              onChange={e => setSubject(e.target.value)}
              required
            />
            <input
              className={classes.text}
              type="email"
              placeholder="Email"
              name="email"
              onChange={e => setEmail(e.target.value)}
              required
            />
            <div className={classes.message_area}>
              <textarea
                style={{
                  fontFamily: "Open Sans"
                }}
                placeholder="How can we help?"
                name="message"
                onChange={e => setMessage(e.target.value)}
                required
              ></textarea>
              <div className={classes.fileInput}>
                <input type="file" id="real-file" hidden="hidden" />
                <button type="button" id="custom-button">
                  CHOOSE A FILE
                </button>
                <span id="custom-text">No file chosen, yet.</span>
                {/* <span id="custom-text">No file chosen, yet.</span>
                <input type="file" id="file" name="myImage" accept="image/*" />
                <label htmlFor="file">
                  <img src={add_image} alt="adds" />
                </label> */}
              </div>
            </div>
            <div className={classes.message_button}>
              <center>
                <input
                  type="submit"
                  className={classes.input}
                  value="Send a message"
                />
              </center>
            </div>
          </div>
        </div>
      </form>
      <div className={classes.link_button} style={{ marginTop: "10px" }}>
        <a href="/">
          <img
            src={help_scout}
            alt="Help Scout logo"
            title="Powered by Help Scout"
          />
        </a>
      </div>
    </>
  );
};

const mapDispatchToProps = dispatch => ({
  addMessage: (name, email, message, subject) =>
    dispatch(addMsg(name, email, message, subject))
});

export default connect(
  null,
  mapDispatchToProps
)(ContactForm);
