import React, { useState } from "react";
import Details from "./components/Details/Details";
import Chat from "./components/Chat/Chat";
import chatIcon from "./assets/comment.svg";
import "./App.css";
function App() {
  let [displayChat, setToggle] = useState(false);

  const toggleChat = () => {
    setToggle(pevState => !displayChat);
  };
  return (
    <div className="App">
      {/* <Details /> */}
      <Chat />
      <div className="chat-icon">
        <img src={chatIcon} alt="Chat Icon" onClick={toggleChat} />
      </div>
    </div>
  );
}

export default App;
