import React, { useState } from "react";
import { connect } from "react-redux";
import classes from "./Login.module.css";
import { login, register } from "../../actions/actions";

const Login = props => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [toggle, setToggle] = useState(true);

  const reset = () => {
    setEmail("");
    setPassword("");
    setName("");
  };
  return (
    <form>
      <div className={classes.Login}>
        {!toggle ? (
          <>
            <label>Username :</label>
            <input
              className={classes.text}
              type="text"
              placeholder="Username"
              name="username"
              value={name}
              onChange={e => setName(e.target.value.trim())}
            />
          </>
        ) : null}
        <label>Email Id :</label>
        <input
          className={classes.text}
          type="email"
          name="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value.trim())}
        />
        <label>Password :</label>
        <input
          className={classes.text}
          type="password"
          name="password"
          value={password}
          placeholder="Password"
          onChange={e => setPassword(e.target.value)}
        />
        {toggle ? (
          <>
            <button onClick={() => props.authLogin(email, password)}>
              Login
            </button>
            <p className={classes.register}>
              Not registered
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setToggle(false);
                  reset();
                }}
              >
                Create an account
              </span>
            </p>
          </>
        ) : (
          <>
            <button
              onClick={() => {
                props.register(name, email, password);
              }}
            >
              Register
            </button>
            <p className={classes.register}>
              Already have an account
              <span
                style={{
                  fontWeight: "bold",
                  cursor: "pointer",
                  margin: "0 5px"
                }}
                onClick={() => {
                  setToggle(true);
                  reset();
                }}
              >
                Login here
              </span>
            </p>
          </>
        )}
      </div>
    </form>
  );
};

const mapDispatchToProps = dispatch => ({
  authLogin: (email, password) => dispatch(login(email, password)),
  register: (name, email, password) => dispatch(register(name, email, password))
});

export default connect(
  null,
  mapDispatchToProps
)(Login);
